import { JsonTransformer } from '@credo-ts/core'
import { SdJwtVcRecord } from '../SdJwtVcRecord'

describe('SdJwtVcRecord', () => {
  test('sets the values passed in the constructor on the record', () => {
    const compactSdJwtVc =
      'eyJhbGciOiJFZERTQSIsInR5cCI6InZjK3NkLWp3dCIsImtpZCI6Ino2TWt0cXRYTkc4Q0RVWTlQcnJ0b1N0RnplQ25ocE1tZ3hZTDFnaWtjVzNCenZOVyJ9..Yz5U__nC0Nccza-NNfqhp-GueKXqeFNjm_NNtC1AJ2KdmERhCHdO6KNjM7bOiruHlo4oAlj-xObuB9LRiKXeCw~WyJzYWx0IiwiaXNfb3Zlcl82NSIsdHJ1ZV0~WyJzYWx0IiwiaXNfb3Zlcl8yMSIsdHJ1ZV0~WyJzYWx0IiwiaXNfb3Zlcl8xOCIsdHJ1ZV0~WyJzYWx0IiwiYmlydGhkYXRlIiwiMTk0MC0wMS0wMSJd~WyJzYWx0IiwiZW1haWwiLCJqb2huZG9lQGV4YW1wbGUuY29tIl0~WyJzYWx0IiwicmVnaW9uIiwiQW55c3RhdGUiXQ~WyJzYWx0IiwiY291bnRyeSIsIlVTIl0~WyJzYWx0IiwiZ2l2ZW5fbmFtZSIsIkpvaG4iXQ~'
    const createdAt = new Date()
    const sdJwtVcRecord = new SdJwtVcRecord({
      id: 'sdjwt-id',
      createdAt,
      tags: {
        some: 'tag',
      },
      compactSdJwtVc,
    })

    expect(sdJwtVcRecord.type).toBe('SdJwtVcRecord')
    expect(sdJwtVcRecord.id).toBe('sdjwt-id')
    expect(sdJwtVcRecord.createdAt).toBe(createdAt)
    expect(sdJwtVcRecord.getTags()).toEqual({
      some: 'tag',
      alg: 'EdDSA',
      sdAlg: 'sha-256',
      vct: 'IdentityCredential',
    })
    expect(sdJwtVcRecord.compactSdJwtVc).toEqual(compactSdJwtVc)
  })

  test('serializes and deserializes', () => {
    const compactSdJwtVc =
      'eyJhbGciOiJFZERTQSIsInR5cCI6InZjK3NkLWp3dCIsImtpZCI6Ino2TWt0cXRYTkc4Q0RVWTlQcnJ0b1N0RnplQ25ocE1tZ3hZTDFnaWtjVzNCenZOVyJ9..Yz5U__nC0Nccza-NNfqhp-GueKXqeFNjm_NNtC1AJ2KdmERhCHdO6KNjM7bOiruHlo4oAlj-xObuB9LRiKXeCw~WyJzYWx0IiwiaXNfb3Zlcl82NSIsdHJ1ZV0~WyJzYWx0IiwiaXNfb3Zlcl8yMSIsdHJ1ZV0~WyJzYWx0IiwiaXNfb3Zlcl8xOCIsdHJ1ZV0~WyJzYWx0IiwiYmlydGhkYXRlIiwiMTk0MC0wMS0wMSJd~WyJzYWx0IiwiZW1haWwiLCJqb2huZG9lQGV4YW1wbGUuY29tIl0~WyJzYWx0IiwicmVnaW9uIiwiQW55c3RhdGUiXQ~WyJzYWx0IiwiY291bnRyeSIsIlVTIl0~WyJzYWx0IiwiZ2l2ZW5fbmFtZSIsIkpvaG4iXQ~'
    const createdAt = new Date('2022-02-02')
    const sdJwtVcRecord = new SdJwtVcRecord({
      id: 'sdjwt-id',
      createdAt,
      tags: {
        some: 'tag',
      },
      compactSdJwtVc,
    })

    const json = sdJwtVcRecord.toJSON()
    expect(json).toMatchObject({
      id: 'sdjwt-id',
      createdAt: '2022-02-02T00:00:00.000Z',
      metadata: {},
      _tags: {
        some: 'tag',
      },
      compactSdJwtVc,
    })

    const instance = JsonTransformer.deserialize(JSON.stringify(json), SdJwtVcRecord)

    expect(instance.type).toBe('SdJwtVcRecord')
    expect(instance.id).toBe('sdjwt-id')
    expect(instance.createdAt.getTime()).toBe(createdAt.getTime())
    expect(instance.getTags()).toEqual({
      some: 'tag',
      alg: 'EdDSA',
      sdAlg: 'sha-256',
      vct: 'IdentityCredential',
    })
    expect(instance.compactSdJwtVc).toBe(compactSdJwtVc)
  })
})
